(ns advent-of-code-2022-clojure.day06
  (:require [clojure.string :as str]))

(def input "")

(def test-input "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")

(defn remove-and-add [curr to-add size]
  (let [new-curr (if (= size (count curr))
                   (drop 1 curr)
                   curr)]
    (conj (into [] new-curr) to-add)))

(defn find-start-of-marker [message size]
  (loop [curr []
         letters (-> message
                     (str/split #""))
         counter 0]
    (if (= size (count curr))
      (let [new-curr (remove-and-add curr (nth letters counter) size)]
        (if (= size (count (distinct new-curr)))
          (inc counter)
          (recur new-curr letters (inc counter))))
      (recur (conj curr (nth letters counter)) letters (inc counter)))))

(find-start-of-marker input 4) ;1876

(find-start-of-marker input 14) ;2202
